    }

    const handleChange = event =>{
        const fileUploaded = event.target.file[0];
        props.handleFile(fileUploaded);
    }

    return(
        <div class="Upload">
            <button onClick={handleClick}>Escolha uma imagem</button>
            <input type="file" class="inputfile" ref={hiddenFileInput} onChange={handleChange}/>
